mapper.select:
SELECT id, category_id, title, base_price, unit, deposit_type, description
FROM pms_service
WHERE id = #{id}

SELECT id, category_id, title, base_price, unit, deposit_type, description
FROM pms_service


        AND category_id = #{categoryId}


        AND title LIKE CONCAT('%', #{title}, '%')


ORDER BY id DESC
LIMIT #{offset}, #{limit}
